
CREATE VIEW dbo.ALP_lkpJmSvcTktSiteId AS 
SELECT TOP 100 PERCENT dbo.ALP_tblArAlpSite.SiteId, dbo.ALP_tblArAlpSite.SiteName + ' (' + CAST(dbo.ALP_tblArAlpSite.SiteId AS varchar(10)) + ')' AS SiteNameId, dbo.ALP_tblArAlpSite.AlpFirstName,dbo.ALP_tblArAlpSite.Addr1, dbo.ALP_tblArAlpSite.Addr2, dbo.ALP_tblArAlpSite.City, COALESCE (dbo.ALP_tblArAlpSite.Addr1, '') + ' ' + COALESCE (dbo.ALP_tblArAlpSite.Addr2, '') + ' ' + COALESCE (dbo.ALP_tblArAlpSite.City, '') + ' ' + COALESCE (dbo.ALP_tblArAlpSite.Region, '') + ' ' + COALESCE (dbo.ALP_tblArAlpSite.PostalCode, '') AS Address, dbo.ALP_tblArAlpSite.Phone, dbo.ALP_tblArAlpSite.Status, dbo.ALP_tblArAlpSite.BranchId, dbo.ALP_tblArAlpSite.TaxLocId, dbo.ALP_tblArAlpSite.SalesRepId1, dbo.ALP_tblArAlpSite.Country, dbo.ALP_tblArAlpSite.PostalCode, dbo.ALP_tblArAlpSite.CreditHoldYn, dbo.ALP_tblArAlpSite.LeadSourceId, dbo.ALP_tblArAlpMarket.DivisionId, dbo.ALP_tblArAlpSite.SiteMemo, dbo.ALP_tblArAlpSite.Taxable FROM dbo.ALP_tblArAlpSite LEFT OUTER JOIN dbo.ALP_tblArAlpMarket ON dbo.ALP_tblArAlpSite.MarketId = dbo.ALP_tblArAlpMarket.MarketId ORDER BY dbo.ALP_tblArAlpSite.SiteName, dbo.ALP_tblArAlpSite.AlpFirstName